import React, { Component } from 'react';

export default class CreateTodo extends Component {
    constructor(props) {
        super(props);

        this.state = {
            topic_description: '',
            topic_link: '',
            topic_rating: '',
            topic_completed: false
        }

        this.onChangeTopicDescription = this.onChangeTopicDescription.bind(this);
        this.onChangeTopiclink= this.onChangeTopicLink.bind(this);
        this.onChangeTopicRating = this.onChangeTopicRating.bind(this);
        this.onSubmit = this.onSubmit.bind(this);
    }

    onChangeTopicDescription(e) {
        this.setState({
            topic_description: e.target.value
        });
    }

    onChangeTopicLink(e) {
        this.setState({
            topic_link: e.target.value
        });
    }

    onChangeTopicRating(e) {
        this.setState({
            topis_priority: e.target.value
        });
    }

    onSubmit(e) {
        e.preventDefault();

        console.log(`Form submitted:`);
        console.log(`Topic Description: ${this.state.topic_description}`);
        console.log(`Topic Link: ${this.state.topic_link}`);
        console.log(`Topic Rating: ${this.state.topic_rating}`);

        this.setState({
            topic_description: '',
            topic_link: '',
            topic_rating: '',
            topic_completed: false
        })
    }

    render() {
        return (
            <div style={{marginTop: 10}}>
                <h3>Create New Topic</h3>
                <form onSubmit={this.onSubmit}>
                    <div className="form-group"> 
                        <label>Description: </label>
                        <input  type="text"
                                className="form-control"
                                value={this.state.topic_description}
                                onChange={this.onChangeTopicDescription}
                                />
                    </div>
                    <div className="form-group">
                        <label>Link: </label>
                        <input 
                                type="text" 
                                className="form-control"
                                value={this.state.topic_link}
                                onChange={this.onChangeTopicLink}
                                />
                    </div>
                    <div className="form-group">
                        <div className="form-check form-check-inline">
                            <input  className="form-check-input" 
                                    type="radio" 
                                    name="ratingOptions" 
                                    id="ratingSucks" 
                                    value="Sucks"
                                    checked={this.state.topic_rating ==='Sucks'} 
                                    onChange={this.onChangeTopicRating}
                                    />
                            <label className="form-check-label">Sucks</label>
                        </div>
                        <div className="form-check form-check-inline">
                            <input  className="form-check-input" 
                                    type="radio" 
                                    name="ratingOptions" 
                                    id="ratingOkayMedium" 
                                    value="Okay" 
                                    checked={this.state.topic_rating ==='Okay'} 
                                    onChange={this.onChangeTopicRating}
                                    />
                            <label className="form-check-label">Okay</label>
                        </div>
                        <div className="form-check form-check-inline">
                            <input  className="form-check-input" 
                                    type="radio" 
                                    name="ratingOptions" 
                                    id="ratingAwesome" 
                                    value="Awesome" 
                                    checked={this.state.topic_rating ==='Awesome'} 
                                    onChange={this.onChangeTopicRating}
                                    />
                            <label className="form-check-label">Awesome</label>
                        </div>
                    </div>

                    <div className="form-group">
                        <input type="submit" value="Create Topic" className="btn btn-primary" />
                    </div>
                </form>
            </div>
        )
    }
}
